import { mat4 } from "gl-matrix";
import { Camera } from "./camera.js";
import { Material } from "./material.js";
import { Geometry, Mesh } from "./mesh.js";
import { Program, ProgramManager } from "./program.js";

let GlobalTexture: null | WebGLTexture = null;
let GlobalGeometry: null | Geometry = null;

export class Glyphy {
    mesh: null | Mesh;

    constructor(gl: WebGLRenderingContext) {
        let program = ProgramManager.getInstance().getProgram("glyphy.vs", "glyphy.fs");

        if (!GlobalGeometry) {
            GlobalGeometry = initGeometry(gl);
        }

        let material = new GlyphyMaterial(gl, program);
        if (!material) {
            throw new Error("material is null");
        }

        this.mesh = new Mesh(gl, GlobalGeometry);
        if (!this.mesh) {
            throw new Error("mesh is null");
        }

        this.mesh.setMaterial(material);
    }

    draw(
        gl: WebGLRenderingContext,
        camera: Camera
    ) {
        if (this.mesh) {
            this.mesh.draw(gl, camera);
        }
    }
}

let isFirstCall = false;

export class GlyphyMaterial extends Material {
    tex: null | WebGLTexture;

    uWorld: mat4;
    uColor: [number, number, number, number];

    program: Program

    tex_w: number;
    tex_h: number;

    constructor(
        gl: WebGLRenderingContext,
        program: Program,
    ) {
        super();

        this.program = program;

        this.tex_w = 512;
        this.tex_h = 512;

        if (!GlobalTexture) {
            GlobalTexture = initTexture(gl, this.tex_w, this.tex_h);
        }
        this.tex = GlobalTexture;

        this.uColor = [1.0, 0.0, 0.0, 1.0];

        this.uWorld = mat4.create();
        mat4.identity(this.uWorld);
    }

    setWorldMatrix(m: mat4) {
        this.uWorld = m;
    }

    setColor(r: number, g: number, b: number, a: number) {
        this.uColor = [r, g, b, a];
    }

    use(
        gl: WebGLRenderingContext,
        camera: Camera
    ) {
        let program = this.program;

        if (!isFirstCall) {
            gl.useProgram(program.id);

            gl.bindTexture(gl.TEXTURE_2D, this.tex);

            let item_w = 64;
            let item_h_q = 8;

            let u_atlas_info = program.getUniform(gl, "u_atlas_info");
            gl.uniform4i(u_atlas_info, this.tex_w, this.tex_h, item_w, item_h_q);

            let u_atlas_tex = program.getUniform(gl, "u_atlas_tex");
            gl.uniform1i(u_atlas_tex, 0);

            let uColor = program.getUniform(gl, "uColor");
            gl.uniform4f(uColor, ...this.uColor);

            let uView = program.getUniform(gl, "uView");
            gl.uniformMatrix4fv(uView, false, camera.uView);

            let uProj = program.getUniform(gl, "uProj");
            gl.uniformMatrix4fv(uProj, false, camera.uProj);
        }
        isFirstCall = true;

        let uWorld = program.getUniform(gl, "uWorld");
        gl.uniformMatrix4fv(uWorld, false, this.uWorld);
    }
}

const initGeometry = (gl: WebGLRenderingContext) => {
    let a_glyph_vertex = [
        -0.081039, 1.229965, 70.000000, 70.000000,
        -0.081039, 0.068864, 70.000000, 71.000000,
        1.080062, 1.229965, 71.000000, 70.000000,
        -0.081039, 0.068864, 70.000000, 71.000000,
        1.080062, 1.229965, 71.000000, 70.000000,
        1.080062, 0.068864, 71.000000, 71.000000,
    ];

    let indices = [0, 1, 2, 3, 4, 5];
    let geometry = new Geometry(gl);

    geometry.setIndices(gl, indices);

    geometry.addAttribute(gl, "a_glyph_vertex", 4, a_glyph_vertex);

    return geometry;
}

